const params = {
  default: {
    host: 'localhost',
    port: 27017,
    db: 'imdb',
    user: 'authen',
    password: 'authen'
  }
};

function makeMongoUri(options) {
  return `mongodb://${options.user}:${options.password}@${options.host}:${options.port}/${options.db}`;
}

module.exports = {
  default: {
    uri: makeMongoUri(params.default)
  }
};
