'use strict';

const express = require('express');
const Routable = require('./routable').Routable;

class Application extends Routable {
    constructor() {
        super(express());
    }

    get expressApp() {
        return this.instance;
    }

    listen(...args) {
        this.expressApp.listen.apply(this.instance, args);

        return this;
    }

    // Abstract
    async prepare() {
        throw new Error('Not implemented');
    }
}

module.exports = {
    Application
};
