'use strict';

const ktoolkit = require('ktoolkit');
const util = require('util');

function watcher(req, res, next) {
    const accessLogger = ktoolkit.logger.access;

    const message = util.format('%s %s %s', req.ip, req.protocol, req.path);
    accessLogger.info(message);

    next();
}

module.exports = {
    watcher
};
